import {useState} from 'react'
import { useTransition, animated } from 'react-spring'
import {Link} from 'react-router-dom'
import React from 'react'
import styled from 'styled-components'

export const Palette = {
    first : "#cce8cc",
    secondary :"#F6EFEE", 
    third : "#e2b6cf",
    fourth : "#e396df", 
    fifth : "#e365c1"
}

const StyledHeader = styled.header`
 .i{
     background-color : ${Palette.third};
     border-radius : 50%;
 }
    background-color : ${Palette.first};
    margin-bottom : 10px;
    box-shadow : 1px 1px 4px grey; 
    font-weight : bold;
    color : white;
`


export default function Header() {
    const [burgerState, openBurger] = useState(false)
    const transition = useTransition(burgerState, {
        from : {},
        enter : {},
        leave : {}
    })
    return (
        <StyledHeader className="navbar d-flex">
            <i className={burgerState === true ? "i bi bi-list" : "i bi bi-x-lg"} onClick={() => openBurger(!burgerState)} alt="Burger menu"/>
        {transition((style,item) => item ? <animated.div style={style}><NavMenu/></animated.div> : null)}
        </StyledHeader>
    )
}

export function NavMenu () {
    return (
        <>
        <Link to="/" className="col-3 col-lg-3">Home</Link>
        <Link to="/Widgets" className="col-3 col-lg-3">Widgets</Link>
        <Link to="/Calculator" className="col-3 col-lg-3">Calculator</Link>
        </>
    )
}
