 import React, {useState, useEffect} from 'react'
 import {animated, useTransition} from 'react-spring'
 export default function Weather() {
    const [fetchedData, setData] = useState(null);
    const [cityChosed, setCity] = useState("")
    const [isOpen, setOpen] = useState(false)
    const transition = useTransition(isOpen, {
        from: {},
        enter : {},
        leave: {}
    });
     useEffect(()=> {Check();})

     const LoadData = async (link) => {
        const response = await fetch(link)
        const data = await response.json()
        setData(data)
        console.log(data)
     }
     function Check () {
    switch (cityChosed) {
            
            case "Paris" :
               LoadData("http://api.weatherapi.com/v1/current.json?key=daa4b9c78d874472a60150746211812&lang=fr&q=Paris")
               setCity("")
                break;
            case "Marseille":
                LoadData("http://api.weatherapi.com/v1/current.json?key=daa4b9c78d874472a60150746211812&lang=fr&q=Marseille")
                setCity("")
                break;  
            case "NewYork" :
                LoadData("http://api.weatherapi.com/v1/current.json?key=daa4b9c78d874472a60150746211812&lang=fr&q=NewYork")
                setCity("")
                break;   
            default : 
            <div>No weather information</div>
        }}
    
     return (
         <>
         <div onClick={() => setOpen(!isOpen)}>
             {transition((style, item) => item ? 
             <animated.div style={style}>
                 <div></div>
                 Open the city selector
                 </animated.div> 
             : 
             <animated.div style={style}>
                 Close city selector
                 <div onClick={() => setCity("Paris")}>
                     Paris
                 </div >
                 <div onClick={() => setCity("NewYork")}>
                     New York
                 </div>
                 <div onClick={() => setCity("Marseille")}>
                     Marseille
                 </div>
             </animated.div>)}
         </div>
         <div>
            {cityChosed === "Paris" ? 
            "FetchedData"
             :
            null}
            {cityChosed === "Marseille" ? "FetchedData" : null}
            {cityChosed === "NewYork" ? "fetchedData" : null}
         </div>
         </>
     )
}
