import React from 'react'
import styled from 'styled-components'
import { Palette } from './Header'
export default function Calculator() {
    return (
        <div className="row">
        <CalcDiv className="offset-lg-4 offset-md-4 offset-1 col-9 col-sm-6 col-md-5 col-lg-3">
            <CalcScreen>
                This is the screen of the calculator    
            </CalcScreen> 
        </CalcDiv>    
        </div>
    )
}
const CalcScreen = styled.div`
    padding : 10px;
    border-radius : 5px;
    margin-top : 10px;
    margin-left : auto;
    margin-right : auto;
    width : 90%;
    height : 80px;
    display : flex;
    text-align : center;
    flex-direction: column;
    justify-content : center;
    background-color : ${Palette.fourth};
`

const CalcDiv = styled.div`
    background-color: ${Palette.fifth};
    height : 500px;
    border-radius : 20px;
`
