import React from 'react'
import styled from 'styled-components'
import { Palette } from './Header'
export default function Calculator(props) {
    let content = [];
    let display
    return (
        <div className="row">
        <CalcDiv className="offset-lg-4 offset-md-4 offset-1 col-9 col-sm-6 col-md-5 col-lg-4 col-xl-3">
            <CalcScreen>
                {display.length}
            </CalcScreen> 
            <CalcGrid>
                <CalcButton>Power</CalcButton>
                <CalcButton>Sqrt</CalcButton>
                <CalcButton>xÂ²</CalcButton>
                <CalcButton>cos()</CalcButton>
                <CalcButton>.</CalcButton>
                <CalcButton onClick={() => display.push(1)}>1</CalcButton>
                <CalcButton>2</CalcButton>
                <CalcButton>3</CalcButton>
                <CalcButton>+</CalcButton>
                <CalcButton>-</CalcButton>
                <CalcButton>4</CalcButton>
                <CalcButton>5</CalcButton>
                <CalcButton>6</CalcButton>
                <CalcButton>*</CalcButton>
                <CalcButton>/</CalcButton>
                <CalcButton>7</CalcButton>
                <CalcButton>8</CalcButton>
                <CalcButton>9</CalcButton>
                <CalcButton>0</CalcButton>
                <CalcButton>=</CalcButton>
            </CalcGrid>
        </CalcDiv>    
        </div>
    )
}
const CalcButton = styled.button`
    background-color: ${Palette.fourth};
    border-radius: 5px;
    padding : 2px;
    box-shadow : 2px 2px 2px black;
    border : 2px solid ${Palette.fourth};
`
const CalcGrid = styled.div`
    width : 90%;
    height : 70%;
    margin-top: 20px;
    margin-left : auto;
    margin-right : auto;
    display : grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    grid-gap : 1px;
`

const CalcScreen = styled.div`
    padding : 20px;
    text-transform: uppercase;
    font-size : 30px;
    text-shadow: 1px 1px 2px grey;
    border-radius : 5px;
    box-shadow : 1px 1px 2px black;
    margin-top : 10px;
    margin-left : auto;
    margin-right : auto;
    width : 90%;
    height : 80px;
    display : flex;
    text-align : center;
    flex-direction: column;
    justify-content : center;
    background-color : ${Palette.fourth};
`

const CalcDiv = styled.div`
    background-color: ${Palette.fifth};
    height : 500px;
    box-shadow : 1px 1px 2px black;
    border-radius : 20px;
`
