import {useState} from 'react'
import { useTransition, animated } from 'react-spring'
import {Link} from 'react-router-dom'
import React from 'react'
import styled from 'styled-components'

export const Palette = {
    first : "#22223b",
    secondary :"#4a4e69", 
    third : "#9a8c98",
    fourth : "#c9ada7", 
    fifth : "#f2e9e4"
}

const StyledHeader = styled.header`
svg {
    height : 100%;
    width : 100%;
}
 .svg{
     padding : 10px;
     border-radius : 50%;
     height : 50px;
    width : 50px;
     background-color : ${Palette.secondary};
 }
    z-index : 2;
    background-color : ${Palette.first};
    font-weight : bold;
    color : white;
`


export default function Header() {
    const [burgerState, openBurger] = useState(false)
    const transition = useTransition(burgerState, {
        from : {x : 1300, position : 'relative' },
        enter : {marginLeft : "80%"},
        leave : {x : 1300, position : 'relative'}
    })
    return (
        <>
        <StyledHeader className="navbar d-flex justify-content-end">
            {burgerState === true ?  <div className="svg" ><svg onClick={() => openBurger(!burgerState)} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-x-lg" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M13.854 2.146a.5.5 0 0 1 0 .708l-11 11a.5.5 0 0 1-.708-.708l11-11a.5.5 0 0 1 .708 0Z"/>
            <path fill-rule="evenodd" d="M2.146 2.146a.5.5 0 0 0 0 .708l11 11a.5.5 0 0 0 .708-.708l-11-11a.5.5 0 0 0-.708 0Z"/>
            </svg></div> : <div className="svg">
            <svg onClick={() => openBurger(!burgerState)} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-list" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"/>
          </svg></div>
}
        </StyledHeader>
         {transition((style,item) => item ? <animated.div className="c" style={style}><NavMenu/></animated.div> : null)}
         </>
    )
}

export function NavMenu () {
    const NavMenuStyles = styled.div`
    background : ${Palette.first};
    display : flex;
    flex-direction: column;
    justify-content: flex-start;
    z-index : 3;
    `
    return (
        <NavMenuStyles>
        <Link to="/" className="col-3 col-lg-3">Home</Link>
        <Link to="/Widgets" className="col-3 col-lg-3">Widgets</Link>
        <Link to="/Calculator" className="col-3 col-lg-3">Calculator</Link>
        </NavMenuStyles>
    )
}
