import React from 'react'
import { useState } from 'react'
import styled from 'styled-components'
import { Palette } from './Header'
export default function Calculator() {
    const [display, setDisplay] = useState("")
    const [part1, setPart1] = useState("")
    const [bool, setBool] = useState(false)
    return (
        <div className="row">
        <CalcDiv className="offset-lg-4 offset-md-4 offset-1 col-9 col-sm-6 col-md-5 col-lg-4 col-xl-3">
            <CalcScreen>{display}</CalcScreen> 
            <CalcGrid>
                <CalcButton>Power</CalcButton>
                <CalcButton>Sqrt</CalcButton>
                <CalcButton>xÂ²</CalcButton>
                <CalcButton>cos()</CalcButton>
                <CalcButton>.</CalcButton>
                <CalcButton onClick={ () => {
                
                    if (b)    setDisplay("1")
                }
                }>1</CalcButton>
                <CalcButton onClick={ () => {
                    
                    setPart1(display + "+")
                    setDisplay("")
                    setBool(true)
                }
                }></CalcButton>
            </CalcGrid>
        </CalcDiv>    
        </div>
    )
}
const CalcButton = styled.button`
    background-color: ${Palette.fourth};
    border-radius: 5px;
    padding : 2px;
    box-shadow : 2px 2px 2px black;
    border : 2px solid ${Palette.fourth};
`
const CalcGrid = styled.div`
    width : 90%;
    height : 70%;
    margin-top: 20px;
    margin-left : auto;
    margin-right : auto;
    display : grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    grid-gap : 1px;
`

const CalcScreen = styled.div`
    padding : 20px;
    text-transform: uppercase;
    font-size : 30px;
    text-shadow: 1px 1px 2px grey;
    border-radius : 5px;
    box-shadow : 1px 1px 2px black;
    margin-top : 10px;
    margin-left : auto;
    margin-right : auto;
    width : 90%;
    height : 80px;
    display : flex;
    text-align : center;
    flex-direction: column;
    justify-content : center;
    background-color : ${Palette.fourth};
`

const CalcDiv = styled.div`
    background-color: ${Palette.fifth};
    height : 500px;
    box-shadow : 1px 1px 2px black;
    border-radius : 20px;
`
