import React, {useState, useEffect} from 'react'
import {animated, useTransition} from 'react-spring'
export default function Weather() {
   const [fetchedData, setData] = useState(null);
   const [cityChosed, setCity] = useState("")
   const [isOpen, setOpen] = useState(false)
   const transition = useTransition(isOpen, {
       from: {},
       enter : {},
       leave: {}
   });
    useEffect(()=> {LoadData();},[])
    const LoadData = async () => {
    const response = await fetch("http://api.weatherapi.com/v1/current.json?key=daa4b9c78d874472a60150746211812&     lang=fr&q=Paris")
   const data = await response.json()
   setData(data)
   console.log(data)
   }
    return (
        <>
        <div onClick={() => setOpen(!isOpen)}>
            {transition((style, item) => item ? 
            <animated.div style={style}>Open the city selector</animated.div> 
            : 
            <animated.div style={style}>
                <div onClick={() => setCity("Paris")}>
                    Paris
                </div >
                <div onClick={() => setCity("NewYork")}>
                    New York
                </div>
                <div onClick={() => setCity("Marseille")}>
                    Marseille
                </div>
            </animated.div>)}
        </div>
        <div>
           {cityChosed == ""}
        </div>
        </>
    )
}
