import React, {useState} from 'react'
import styled from 'styled-components'

export default function Color() {
    const [color , setColor] = useState("#")
    function ColorGen() {
        const range = [1,2,3,4,5,6,7,8,9,"A","B","C","D","E"];
        
        function Gen () {
           return Math.round(Math.random() * 14);
        } 
        for (let i=0; i < 6; i++){
            let result = 0;
            result = Gen();
        }
        setColor(color + range[result])
}
    return (
        <ColorDiv>
        <button onClick={ColorGen}/>
        <p>{color}</p>    
        </ColorDiv>
    )
}

const ColorDiv = styled.div`
    width : 100%;
    height : 100%;
    
`
