import React from 'react'
import { useState } from 'react'
import styled from 'styled-components'
import { Palette } from './Header'

function Buttons () {
    return (
        <CalcGrid>
                <CalcButton  className="DEL" onClick={() => setDisplay("")}>DEL</CalcButton>
                <CalcButton onClick={() => setDisplay(`Math.sqrt(${display})`)}>Sqrt</CalcButton>
                <CalcButton onClick={() => setDisplay(display + ` * ${display}`)}>x²</CalcButton>
                <CalcButton onClick={() => setDisplay(`Math.cos(${display})`)}>cos()</CalcButton>
                <CalcButton onClick={() => setDisplay(display + ".")}>.</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "1")}>1</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "2")}>2</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "3")}>3</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "+")}>+</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "-")}>-</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "4")}>4</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "5")}>5</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "6")}>6</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "*")}>*</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "/")}>/</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "7")}>7</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "8")}>8</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "9")}>9</CalcButton>
                <CalcButton onClick={() => setDisplay(display + "0")}>0</CalcButton>
                <CalcButton className="EQUAL" onClick={() => setDisplay( 
                    //eslint-disable-next-line 
                eval(display)
                )}>=</CalcButton>
            </CalcGrid>
    )
}
export default function Calculator() {
    const [display, setDisplay] = useState("")


    return (
        <div className="row">
        <CalcDiv className="offset-lg-4 offset-md-4 offset-1 col-9 col-sm-6 col-md-5 col-lg-4 col-xl-3">
            <CalcScreen>{display}</CalcScreen> 
            
        </CalcDiv>    
        </div>
    )
}
const CalcButton = styled.button`

    background-color: ${Palette.third};
    border-radius: 5px;
    padding : 2px;
    box-shadow : 2px 2px 2px black;
    border : 2px solid ${Palette.fourth};
`
const CalcGrid = styled.div`
.DEL {
    background-color: red;
    color : white;
}
.EQUAL {
    background-color : white;
    color : black;
}
    width : 90%;
    height : 70%;
    margin-top: 20px;
    margin-left : auto;
    margin-right : auto;
    display : grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    grid-gap : 1px;
`

const CalcScreen = styled.div`
    padding : 20px;
    text-transform: uppercase;
    font-size : 30px;
    text-shadow: 1px 1px 2px grey;
    border-radius : 5px;
    box-shadow : 1px 1px 2px black;
    margin-top : 10px;
    margin-left : auto;
    margin-right : auto;
    width : 90%;
    height : 80px;
    display : flex;
    text-align : center;
    flex-direction: column;
    justify-content : center;
    background-color : ${Palette.fourth};
`

const CalcDiv = styled.div`
    background-color: ${Palette.fifth};
    height : 500px;
    box-shadow : 1px 1px 2px black;
    border-radius : 20px;
`
